mapper.select:

select id, gmt_created, gmt_modified, gmt_deleted, remark, username, password, enabled, email
from user
where id = #{id,jdbcType=INTEGER}

select username, password
from user
where username = #{username}

select id, gmt_created, gmt_modified, gmt_deleted, remark, username, password, enabled, email
from user
where email=#{email} and gmt_deleted is null;
